data.import-scss-file-and-wrap-your-app#Impor:
import '@atlrdsgn/kit/styles'
import '...other_styles.css'

import { KitProvider } from '@atlrdsgn/kit'

export default function App({ Component, pageProps }) {
return (
<KitProvider>
<Component {...pageProps} />
</KitProvider>
)
}

